def user_prompt
  print "\e[1m\e[31m> "
  input = gets.chomp.downcase
  puts "\e[0m"
  return input
end

def return_to_menu
  loop do
    print "Veux-tu revenir au menu ? \e[1mY/N\e[0m  "
    input = user_prompt
    if input == "y" || input == "yes"
      menu
      break
    elsif input == "n" || input =="no"
      puts "Merci d'avoir utilisé ce petit logiciel \u{1F600}\n"
      break
    else
      puts "\e[41m\e[1mEntrée invalide.\e[0m\n\n"
    end
  end
end

def handles_quantity
  puts "\e[1m\e[42mIl y a #{TWITTER_HANDLES.length} handles dans l'array\e[0m\n\n"
  return_to_menu
end

def shortest_handle
  shortest_element = TWITTER_HANDLES.min_by { |element| element.length }
  puts "\e[1m\e[42mHandle le plus court : #{shortest_element}\e[0m\n\n"
  return_to_menu
end

def five_characters_handles
  number = 0
  TWITTER_HANDLES.each do |element|
    if element.length == 6
      number += 1
    end
  end
  puts "\e[1m\e[42mNombres de handles contenant 5 caractères : #{number}\e[0m\n\n"
  return_to_menu
end

def uppercase_handles
  number = 0
  TWITTER_HANDLES.each do |element|
    second_character = element[1]
    if second_character == second_character.upcase
      number += 1
    end
  end
  puts "\e[1m\e[42mNombres de handles commençant par une majuscule : #{number}\e[0m\n\n"
  return_to_menu
end

def handles_by_az
  sorted_handles = TWITTER_HANDLES.sort
  puts "\e[1m\e[32m#{sorted_handles.join("\n")}\e[0m\n\n"
  return_to_menu
end

def handles_by_size
  sorted_handles = TWITTER_HANDLES.sort_by { |element| element.length }
  puts "\e[1m\e[32m#{sorted_handles.join("\n")}\e[0m\n\n"
  return_to_menu
end

def epenser_position
  index = TWITTER_HANDLES.index("@epenser")
  puts "\e[1m\e[42m@epenser est à la position #{index} dans l'array\e[0m\n\n"
  return_to_menu
end

def size_repartition
  repartition = TWITTER_HANDLES.group_by { |element| element.length }
  sorted_repartition = repartition.sort_by { |length, _elements| length }.to_h

  sorted_repartition.each do |length, elements|
    puts "\e[1m\e[42mHandles de #{length - 1} caractères :\e[0m\e[32m #{elements.join(', ')}\e[0m\n\n"
  end
  return_to_menu
end

def menu
  loop do
    puts "\n\e[33m\e[4mChoisis une fonction à exécuter\e[0m\n\n\e[41m1\e[0m Nombre de handles dans l'array\n\e[42m2\e[0m Handle le plus court\n\e[43m3\e[0m Nombre de handles contenant 5 caractères\n\e[44m4\e[0m Handles commençant par une majuscule\n\e[45m5\e[0m Handles par ordre alphabétique\n\e[46m6\e[0m Handles par taille\n\e[41m7\e[0m Position de @epenser\n\e[42m8\e[0m Répartition des handles par taille\n\e[47mX\e[0m Quitter"
    input = user_prompt
    case input
    when "1"
      handles_quantity
      break
    when "2"
      shortest_handle
      break
    when "3"
      five_characters_handles
      break
    when "4"
      uppercase_handles
      break
    when "5"
      handles_by_az
      break
    when "6"
      handles_by_size
      break
    when "7"
      epenser_position
      break
    when "8"
      size_repartition
      break
    when "x", "X"
      break
    else
      puts "\e[41m\e[1mEntrée invalide.\e[0m"
      retry_flag = true
    end
    break unless retry_flag
    retry_flag = false
  end
end

menu